import React, { Component } from 'react'
import axios from 'axios';


export default class EditBug extends Component {
    constructor(props) {
        super(props);

        this.state = {
            description: '',
            assignee: '',
            priority: '',
            completed: false
        }

        this.onChangeDesc = this.onChangeDesc.bind(this);
        this.onChangeAssignee = this.onChangeAssignee.bind(this);
    }

    componentDidMount() {
        axios.get('http://localhost:4000/bugs/'+this.props.match.params.id)
            .then(res => {
                this.setState({
                    description: res.data.description,
                    assignee: res.data.assignee,
                    priority: res.data.priority,
                    completed: res.data.completed
                })

            })
            .catch(function(error) {
                console.log(error)
            })
    }

    onChangeDesc(e) {
        this.setState({
            description: e.target.value 
        });
    }

    onChangeAssignee(e) {
        this.setState({
            assignee: e.target.value
        })
    }

    render() {
        return (
            <div>
                <h1>Update a bug </h1>
                <form onSubmit={this.onSubmitDesc}> 
                    <div className="form-group">
                        <label>Description</label>
                            <input type="text"
                                    className="form-group"
                                    value={this.state.description}
                                    onChange={this.onChangeDesc} />
                    </div>

                    <div className="form-group">
                            <label>Completed: </label>
                            <input type="text"
                                    className="form-control"
                                    value={this.state.completed}
                                    onChange={this.onChangeCompleted}
                                />
                        </div>
                        <div className="form-group">
                        <div class="form-check form-check-inline">

                            <input class="form-check-input" 
                                    type="radio" 
                                    value="High" 
                                    checked={this.state.onChangePriority==='High'}
                                    onChange={this.onChangePriority}
                                    id="high"
                                    />
                            <label class="form-check-label" for="High">High</label>

                            <input class="form-check-input" 
                                    type="radio" 
                                    value="Medium"
                                    checked={this.state.onChangePriority==='Medium'}
                                    onChange={this.onChangePriority}
                                    id="Medium"/>
                            <label class="form-check-label" for="Medium">Medium</label>

                            <input class="form-check-input" 
                                    type="radio" 
                                    value="Low"
                                    checked={this.state.onChangePriority==='Low'}
                                    onChange={this.onChangePriority}
                                    id="Low"/>
                            <label class="form-check-label" for="Low">Low</label>
                        </div>

                        </div>

                        <div className="form-group">
                            <label>Assignee: </label>
                            <input type="text"
                                    className="form-control"
                                    value={this.state.assignee}
                                    onChange={this.onChangeAssignee}
                                />
                        </div>

                        <div className="form-group">
                            <input type="checkbox"
                                    className="form-check-input"
                                    id="completedCheckbox"
                                    name="completedCheckbox"
                                    onChange={this.onChangeCompleted}
                                    checked={this.state.completed}
                                    value={this.state.completed}
                                    /> 
                            <label className="form-check-label" htmlFor="completedCheckbox">
                                Completed
                            </label>
                        </div>

                        <div className="form-group">
                            <input type="submit" value="UPdate todo" className="btn btn-primary" />
                        </div>
                </form>
            </div>
        )
    }
}
