import React, { Component } from 'react'
import axios from 'axios'
export default class Search extends Component {

    constructor( props ) {
        super( props );
        this.state = {
            query: '',
            data: this.props.bugs

        }

        this.runSearch = this.runSearch.bind(this);
        this.onChangeHandler = this.onChangeHandler.bind(this);

    }

    onChangeHandler(e) {

        this.setState({
            query: e.target.value
        })
    }


    runSearch(e) {
        e.preventDefault();
        console.log("Search value is : " + e.target.value)

        this.setState({
            query: e.target.value

        })

        console.log("State value query " + this.state.query);

        axios.get(`http://localhost:4000/bugs/buglist/search?q=${this.state.query}`)
            .then(res => {
                this.setState({
                    q: this.state.q,
                    data: res.data
                })
            }).catch(error => {
                console.log("Error " + error);
            })
    }

    render() {

        const findQuery = this.state.data.filter(
            i => i.description === this.state.query
        )

        return (
            <div>

                <input
                        //onSubmit={this.runSearch}
                        onChange={this.runSearch}
                        type="text"
                        placeholder="Search..."
                    />

            </div>
        )
    }
}

