import React, { Component } from 'react'
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import axios from 'axios';

export default class CreateProject extends Component {
    constructor(props) {
        super(props);

        this.state = {
            projectName: '',
            description: '',
            members: [],
            dateCreated: new Date(),

        }

        this.onChangeDescription = this.onChangeDescription.bind(this);
        this.onChangeDateCreated = this.onChangeDateCreated.bind(this);
        this.onChangeProjectName = this.onChangeProjectName.bind(this);
        this.onChangeMembers = this.onChangeMembers.bind(this);

        this.onSubmitForm = this.onSubmitForm.bind(this);

    }


    // Get all the users info from User database.
    // componentDidMount() {
    //     axios.get('http://localhost:4000/bugs/api/users')
    //     .then(res => { console.log(res.data)
    //         // if(res.data.length > 0) {
    //         //     this.setState({
    //         //         members: res.data.map(member => member.name)
    //         //     })
    //         // }
    //     })
    //     .catch((error) => {
    //         console.log(error);
    //     })
    // }

    onChangeDescription(e) {
        this.setState({
            description: e.target.value
        });
    }


    onChangeProjectName(e) {
        this.setState({
            projectName: e.target.value
        });
    }

    onChangeMembers(e) {
        this.setState({
            members: e.target.value
        });
    }

    onChangeDateCreated(date) {

        this.setState({
            dateCreated: date
        });
    }

    onSubmitForm(e) {
        e.preventDefault();

        console.log("form submitted");
        console.log(`description: ${this.state.projectName}`);
        console.log(`date: ${this.state.dateCreated}`);
        console.log(`Priority: ${this.state.description}`);
        console.log(`assignee: ${this.state.members}`);

        const newProject = {
            description: this.state.description,
            dateCreated: this.state.dateCreated.toString(),
            members: this.state.members,
            projectName: this.state.projectName

        }


        //Create a new project
        axios.post('http://localhost:4000/projects/createProj', newProject)
            .then(res => console.log(res.data),
            this.props.history.push("/projects"));

        this.setState({
            description: '',
            dateCreated: new Date(),
            projectName: '',
            members: [],
            memberName: ''

        });





    }

    render() {
        return (
            <div className="container">
                <h3>Create a Project</h3>
                <div style={{marginTop: 20}}>
                    <form onSubmit={this.onSubmitForm}>
                        <div className="form-group">
                            <label>Description: </label>
                            <input type="text"
                                    className="form-control"
                                    value={this.state.description}
                                    onChange={this.onChangeDescription}
                                />
                        </div>

                        <div className="form-group">
                            <label>Date Created: </label>
                                <div>
                                    <DatePicker
                                        selected={this.state.dateCreated}

                                        onChange={this.onChangeDateCreated}
                                    />
                                </div>
                        </div>

                        <br />
                        <div className="form-group">
                            <label>Project Name: </label>
                            <input type="text"
                                    className="form-control"
                                    value={this.state.projectName}
                                    onChange={this.onChangeProjectName}
                                />
                        </div>

                        <br />

                        <div className="form-group">
                            <label>Member: </label>
                            <select ref="userInput"
                            required
                            className="form-control"
                            value={this.state.members}
                            onChange={this.onChangeMembers}>
                                {this.state.members.map(function(user) {
                                    return <option
                                        key={user}
                                        value={user}>{user}
                                    </option>;
                                })}
                            </select>
                        </div>

                        <div className="form-group">
                            <input type="submit" value="Create Project" className="btn btn-primary" />
                        </div>

                    </form>

                </div>
            </div>
        )
    }
}
