import React, { Component, Link } from 'react'
import axios from 'axios';


const Project = props => (
    <tr>
        <td className={props.project.completed ? 'completed' : ''}>{props.project.description}</td>
        <td className={props.project.completed ? 'completed' : ''}>{props.date}</td>
        <td className={props.project.completed ? 'completed' : ''}>{props.project.assignee}</td>
        <td className={props.project.completed ? 'completed' : ''}>{props.project.priority}</td>

        <td>
            <Link to={"/edit/"+props.bug._id} >Edit </Link> | <a href="#" onClick={() => { props.deleteBug(props.bug._id) }}> delete</a> 
        </td>


    </tr>
)
export default class Projects extends Component {
    constructor(props) {
        super(props);

        //this.deleteBug = this.deleteBug.bind(this);

        this.state = {
            projects: []
        };
    }
    componentDidMount() {
        axios.get('http://localhost:4000/bugs/projects')
            .then(response => {
                this.setState({
                    projects: response.data});

            })
            .catch(function(error) {
                console.log(error)
            })

    }

    componentDidUpdate() {
        axios.get('http://localhost:4000/bugs/projects')
        .then(response => {
            this.setState({
                projects: response.data});
        })
        .catch(function(error) {
            console.log(error)

        })

}

    // deleteBug(id) {
    //     axios.delete('http://localhost:4000/bugs/'+id) 
    //         .then(response => {console.log(response.data)});

    //     this.setState({
    //         bugs: this.state.bugs.filter(el => el._id !== id)
    //     })
    // }

    projectList() {
        return this.state.projects.map((currentProject) =>{

            return <Project project={currentProject}
                        //deleteBug={this.deleteBug}
                        key={currentProject._id}
                        date={currentProject.date.substring(0,10)} />;
        });
    }

    render() {
        return (
            <div className="container">
                <h3> Projects </h3>
                <table className="table table-striped"
                    style={{marginTop: 20 }}>
                        <thead>
                            <tr>
                                <th>Description</th>
                                <th>Deadline</th>
                                <th>Assignee</th>
                                <th>Priority</th>
                                <th>Actions</th>
                            </tr>
                        </thead>
                        <tbody>
                            {this.projectList()}
                        </tbody>

                    </table>
            </div>
        )
    }
}
