import React, { Component } from 'react';
import "bootstrap/dist/css/bootstrap.min.css";
import './login.component.css';
import {
    setInStorage,
    getFromStorage,
} from '../config/utils/storage'
import axios from 'axios';

export default class Login extends Component {
    constructor(props) {
    super(props);

    this.state = {
        isLoading: true,
        token: '',
        signUpError: '',
        signInError: '',
        signInEmail: '',
        signInPassword: '',
        signUpEmail: '',
        signUpPassword: '',
    };

    this.onTextboxChangeSignInEmail = this.onTextboxChangeSignInEmail.bind(this);
    this.onTextboxChangeSignInPassword = this.onTextboxChangeSignInPassword.bind(this);
    this.onTextboxChangeSignUpEmail = this.onTextboxChangeSignUpEmail.bind(this);
    this.onTextboxChangeSignUpPassword = this.onTextboxChangeSignUpPassword.bind(this);
    this.onSignUp = this.onSignUp.bind(this);
    this.onSignIn = this.onSignIn.bind(this);
    this.logout = this.logout.bind(this);
}

componentDidMount() {
    const obj = getFromStorage('the_main_app');
        if (obj && obj.token) {
            console.log('Object: ' + obj);
            console.log('Token: ' + obj.token);
            const { token } = obj;
        // Verify token
        fetch('/api/account/verify?token=' + token)
            .then(res => res.json())
            .then(json => {
            if (json.success) {
                this.setState({
                    token,
                    isLoading: false
                });
            } else {
                this.setState({
                isLoading: false,
                });
            }
        });
} else {
    this.setState({
        isLoading: false
    });
}
}

onTextboxChangeSignInEmail(event) {
    this.setState({
        signInEmail: event.target.value
    });
}

onTextboxChangeSignInPassword(event) {
    this.setState({
        signInPassword: event.target.value
    });
}

onTextboxChangeSignUpEmail(event) {
    this.setState({
        signUpEmail: event.target.value
    });
}

onTextboxChangeSignUpPassword(event) {
    this.setState({
        signUpPassword: event.target.value
    });
}

onSignUp(e) {
    e.preventDefault();


    console.log("form submitted");
    console.log(`signUpEmail: ${this.state.signUpEmail}`);
    console.log(`signUpPassword: ${this.state.signUpPassword}`);

    const newUser = {
        signUpEmail: this.state.signUpEmail,
        signUpPassword: this.state.signUpPassword
    }

    this.setState({
        isLoading: true
    });
      // Post request to backend
    axios.post('http://localhost:4000/bugs/api/account/signup/', newUser)
            .then(response => console.log(response.data))
            .catch(err => console.log("Error" + err));

    this.setState({    
        isLoading: false,
        signUpEmail: '',
        signUpPassword: ''
    });


}


    onSignIn() {
            // Grab state
            const { signInEmail, signInPassword } = this.state;
            this.setState({
                isLoading: true
            }); 
            // Post request to backend
            fetch('/api/account/signin/', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                email: signInEmail,
                password: signInPassword,
            }),
            }).then(res => res.json())
            .then(json => {
                console.log('json', json);
                if (json.success) {
                    setInStorage('the_main_app', { token: json.token });
                this.setState({
                    signInError: json.message,
                    isLoading: false,
                    signInPassword: '',
                    signInEmail: '',
                    token: json.token
                });
                } else {
                this.setState({
                    signInError: json.message,
                    isLoading: false
                });
                }
            });
        }

        logout() {
            this.setState({
                isLoading: true,
                });
                const obj = getFromStorage('the_main_app');
                if (obj && obj.token) {
                const { token } = obj;
                // Verify token
                fetch('/api/account/logout?token=' + token)
                    .then(res => res.json())
                    .then(json => {
                    if (json.success) {
                        this.setState({
                        token: '',
                        isLoading: false
                        });
                    } else {
                        this.setState({
                        isLoading: false,
                        });
                    }
                    });
                } else {
                this.setState({
                    isLoading: false,
                });
                }
            }

render() {
        const {
        isLoading,
        token,
        signInError,
        signInEmail,
        signInPassword,
        signUpEmail,
        signUpPassword,
        signUpError
        } = this.state;

        if (isLoading) {
            return (<div><p>Loading...</p></div>);
        }
        if (!token) {
        return (
            <div>
            <div>
                {
                    (signInError) ? (<p>{signInError}</p>) : (null)
                }
                <p>Sign In</p>
                <input
                    type="email"
                    placeholder="Email"
                    value={signInEmail}
                    onChange={this.onTextboxChangeSignInEmail}
                />
                <br />
                <input
                    type="password"
                    placeholder="Password"
                    value={signInPassword}
                    onChange={this.onTextboxChangeSignInPassword}
                />
                <br />
                <button onClick={this.onSignIn}>Sign In</button>
            </div>
            <br />
            <br />
            <form onClick={this.onSignUp} >
            <div className="form-group">
                <h3>Sign Up</h3>
                <input
                    type="email"
                    placeholder="Email"
                    value={signUpEmail}
                    onChange={this.onTextboxChangeSignUpEmail}
                />
                <br />
                <input
                    type="password"
                    placeholder="Password"
                    value={this.state.signUpPassword}
                    onChange={this.onTextboxChangeSignUpPassword}
                />
                <br />
                <div className="form-group">
                    <input type="submit" value="Sign up" className="btn btn-primary" />
                </div>                
            </div>
            </form>
        </div>
        );
            }

    }
}
