import React, { Component } from 'react'
import { Link } from 'react-router-dom';
import axios from 'axios'


const Bug = props => (
    <tr>
        <td>{props.bug.description}</td>
        <td>{props.date}</td>
        <td>{props.bug.assignee}</td>
        <td>{props.bug.priority}</td>

        <td>
            <Link to={"/edit/"+props.bug._id} >Edit </Link> | <a href="#" onClick={() => { props.deleteBug(props.bug._id) }}> delete</a> 
        </td>
    </tr>
)

export default class ViewProjectBugs extends Component {
    constructor(props) {
        super(props);

        this.state = {
            bugsList: [],
            data: []

        }
    }

    componentDidMount() {
        axios.get('http://localhost:4000/projects/bugs/'+this.props.match.params.id)
            .then(response => {
                this.setState({
                    bugsList: response.data});
                    console.log("Did Mount here" + JSON.stringify(response.data));

            }).then(axios.get('http://localhost:4000/bugs/')
                .then(response => {
                        this.setState({
                            data: response.data});
                            console.log("Data Mount here" + JSON.stringify(response.data));

                    }))
                    .catch(function(error) {
                        console.log(error)
                    })
    }

    render() {
        let bugDescription = this.state.data.map((currentBug) => {
            return <Bug key={currentBug.id} 
                        bug={currentBug}
            />});

        //console.log("result ",this.state.data);

        console.log("Bugs array here",this.state.bugsList);
        return (
            <div>
                <h3>{this.state.bugsList.projectName}</h3>

                <table className="table"
                    style={{marginTop: 20 }}>
                        <thead>buglist
                            <tr>
                                <th>Description</th>
                                <th>Deadline</th>
                                <th>Assignee</th>
                                <th>Priority</th>
                                <th>Actions</th>
                            </tr>
                        </thead>
                        <tbody>{bugDescription}</tbody>
                </table>

            </div>  
        )
    }
}
