import React, { Component } from 'react'
import axios from 'axios'
export default class Search extends Component {

    constructor( props ) {
		super( props );

        this.setQuery.bind(this);
        this.setState.bind(this);
        this.runSearch.bind(this);

    }

    componentWillUpdate() {
        this.setState({
            q: "",
            data:[]
        })
    }

    setQuery(e) {
        this.setState({
            q: e.target.value,
            data: this.state.data
        })
    }

    runSearch(e) {
        e.preventDefault();

        axios.get(`http://localhost:4000/bugs/buglist/search?q=${this.state.q}`)
            .then(res => {
                this.setState({
                    q: this.state.q,
                    data: res.data
                })
            }).catch(error => {
                console.log("Error " + error);
            })
    }

    render() {
        return (
            <div>
                <input
                        onSubmit={e => this.runSearch(e)}
                        onChange={e => this.setQuery(e)}
                        type="text"
                        placeholder="Search..."
                    />
                {this.state.data.map(found => {
                    return <li key={found}>{found}</li>
                })}
            </div>
        )
    }
}

