import React, { Component } from 'react'
import axios from 'axios'
export default class Search extends Component {

    constructor( props ) {
		super( props );

        this.setQuery.bind(this);
        this.setState.bind(this);
        this.runSearch.bind(this);

    }

    componentWillUpdate() {
        this.setState({
            q: "",
            data:[]
        })
    }

    setQuery(e) {
        this.setState({
            q: e.target.value,
            data: this.state.data
        })
    }
}
