import React, { Component } from 'react'

export default class CreateBug extends Component {
    constructor(props) {
        super(props);

        this.state = {
            description: '',
            completed: false,
            priority: '',
            dateCreated: new Date(),
            assignee: ''
        }
        this.onSubmitForm = this.onSubmitDescription.bind(this);
        this.onChangeDescription = this.onChangeDescription.bind(this);
    }

    onChangeDescription(e) {
        this.setState({
            description: e.target.value
        });
    }
    onChangePriority(e) {
        this.setState({
            priority: e.target.value
        });
    }

    onChangePriority(e) {
        this.setState({
            priority: e.target.value
        });
    }

    onChangeDescription(e) {
        this.setState({
            description: e.target.value
        });
    }

    onSubmitForm(e) {
        e.preventDefault();

        console.log("form submitted");
        console.log(`description: ${this.state.description}`);


        this.setState({
            description: '',
            completed: false,
            priority: '',
            dateCreated: new Date(),
            assignee: ''
        });

    }

    render() {
        return (
            <div>
                <h1>Create a Bug</h1>
            </div>
        )
    }
}
