import React, { Component } from 'react'
import axios from 'axios'

export default class ViewProjectBugs extends Component {
    constructor(props) {
        super(props);

        this.state = {
            bugsList: [],


        }
    }

    componentDidMount() {
        axios.get('http://localhost:4000/projects/bugs/'+this.props.match.params.id)
            .then(response => {
                this.setState({
                    bugsList: response.data});
                    console.log("Did Mount here" + JSON.stringify(response.data));

            })
            .catch(function(error) {
                console.log(error)
            })

            console.log("Bugs array here",this.state.bugsList.bugs);

    }



    // componentDidMount() {
    //     axios.get('http://localhost:4000/bugs/')
    //         .then(response => {
    //             this.setState({
    //                 bugsList: response.data});
    //                 console.log("Did Mount here" + JSON.stringify(response.data));

    //         })
    //         .catch(function(error) {
    //             console.log(error)
    //         })
    // }

    getBugs() {
        let bugs = {...this.state.bugsList.bugs};
        let result = [];

        Object.entries(bugs).forEach(([key,value])=>{
            axios.get('http://localhost:4000/bugs/'+`${value}`)
                .then(response => {
                    console.log("response" + JSON.stringify(response.data));
                    result.push(response.data);
                })
                .catch(function(error) {
                    console.log(error);
                })
            console.log("Bugs array", bugs);
        })


    }

    render() {
        this.getBugs();



        console.log("Bugs array here",this.state.bugsList.bugs);
        return (
            <div>
                <h3>{this.state.bugsList.projectName}</h3>
                {/* <div>{bugs}</div> */}
            </div>  
        )
    }
}
