import React, { Component } from 'react'
//import "bootstrap/dist/css/bootstrap.min.css";
import { BrowserRouter as Router, Route, Switch, Link } from "react-router-dom";
import jwt_decode from "jwt-decode";
import setAuthToken from "./utils/setAuthToken";
import { setCurrentUser, logoutUser } from "./actions/authActions";
import './App.css';

import { Provider } from "react-redux";
import store from "./store";
import Navbar from "../src/components/layout/Navbar";

import Wrapper from "../src/components/layout/Wrapper";
import CreateBug from "../src/components/bugtrack/createbug.component";
import BugList from "../src/components/bugtrack/buglist.component"
import EditBug from "../src/components/bugtrack/editbug.component";
import Projects from "../src/components/projects/projectlist";
import CreateProject from "../src/components/projects/createproject";
import EditProject from "../src/components/projects/editproject.component";
import UsersList from "../src/components/usersProfile/userList.component";
import EditUsers from "../src/components/usersProfile/editUsers.component";
import ViewProjectBugs from "../src/components/projects/viewProjectBugs";
import Notfound from "./components/Notfound";
import Landing from "../src/components/layout/Landing"
import Register from "./components/auth/Register";
import Login from "./components/auth/Login";
import PrivateRoute from "./components/private-route/PrivateRoute";
import Dashboard from "./components/dashboard/Dashboard"

if (localStorage.jwtToken) {
  // Set auth token header auth
  const token = localStorage.jwtToken;
  setAuthToken(token);
  // Decode token and get user info and exp
  const decoded = jwt_decode(token);
  // Set user and isAuthenticated
  store.dispatch(setCurrentUser(decoded));
// Check for expired token
  const currentTime = Date.now() / 1000; // to get in milliseconds
  if (decoded.exp < currentTime) {
    // Logout user
    store.dispatch(logoutUser());
    // Redirect to login
    window.location.href = "/login";
  }
}

const routes = [
  {
    path: '/',
    exact: true,
    sidebar: () => <div>Home</div>,
    main: () => <h2>Home</h2>
  },
  {
    path: '/buglist',
    sidebar: () => <div><Navbar/></div>,
    main: () => <h2>Buglist</h2>
  },
  {
    path: "/edit/:id",
    sidebar: () => <div>EditBug</div>,
    main: () => <h2>EditBug</h2>
  },
  {
    path: "/projects/bugs/:id",
    sidebar: () => <div>Create Bug</div>,
    main: () => <h2>Create bug</h2>
  },
  {
    path: "/projects",
    sidebar: () => <div>Projects list</div>,
    main: () => <h2>Projects</h2>
  },
  {
    path: "/editProj/:id",
    sidebar: () => <div>Edit project</div>,
    main: () => <h2>Edit project</h2>
  },
  {
    path: "/createProj",
    sidebar: () => <div>Create project</div>,
    main: () => <h2>Create project</h2>
  },
  {
    path: "/usersList",
    sidebar: () => <div>User List</div>,
    main: () => <h2>shoelace</h2>
  },
  {
    path: "/api/users/:id",
    sidebar: () => <div>Edit Users</div>,
    main: () => <h2>Edit users</h2>
  },
  {
    path: "/bugs/:id" ,
    sidebar: () => <div>View Projects Bugs</div>,
    main: () => <h2>View Projects Bugs</h2>
  },
  {
    path: "/dashboard",
    sidebar: () => <div>Dashboard</div>,
    main: () => <h2>Dashboard</h2>
  }
]


export default class App extends Component {
  render() {
    return (



            <Provider store={store}>
            <Router>
            <Switch>
              <Route exact path="/" component={Landing} />

              <Route exact path="/login" component={Login} />
              <Route exact path="/register" component={Register} />


              <div className="d-flex" id="wrapper">
                <div className="bg-dark border-right" id="sidebar-wrapper">
                    <Link to="/projects" className="navbar-brand text-white">Bug Tracker</Link>
                        <div className="list-group list-group-flush">
                                <a href="/buglist" className="list-group-item list-group-item-action bg-dark text-white"><i class="fas fa-bug"></i>Show Bugs</a>

                                <a href="/projects" className="list-group-item list-group-item-action bg-dark text-white"><i class="fas fa-tasks"></i>Show Projects</a>
                                <a href="/createProj" className="list-group-item list-group-item-action bg-dark text-white"><i class="far fa-handshake"></i>Create projects</a>
                                <a href="/usersList" className="list-group-item list-group-item-action bg-dark text-white"><i class="fas fa-users"></i>Show users</a>
                                <a href="/dashboard" className="list-group-item list-group-item-action bg-dark text-white" ><i class="fas fa-chart-bar"></i>Show Dashboard</a>

                                <button className="btn btn-secondary" onClick={this.onLogoutClick}>Logout</button>

                        </div>


                    </div>

            {routes.map((route) => (
              <PrivateRoute
                key={route.path}
                path={route.path}
                exact={route.exact}
                component={route.siderbar} />
            ))}

            {/* <PrivateRoute path="/buglist" component={BugList}/>
                <PrivateRoute exact path="/edit/:id" component={EditBug} />
                <PrivateRoute exact path="/projects/bugs/:id" component={CreateBug} />

                <PrivateRoute exact path="/projects" component={Projects}/>
                <PrivateRoute exact path="/editProj/:id" component={EditProject} />
                <PrivateRoute path="/createProj" component={CreateProject} />

                <PrivateRoute path="/usersList" component={UsersList} />
                <PrivateRoute path="/api/users/:id" component={EditUsers} />
                <PrivateRoute path="/bugs/:id" component={ViewProjectBugs} />

                <PrivateRoute path='/dashboard' component={Dashboard} />

                <Route component={Notfound} /> 
                 */}
                 </div>
                </Switch>


                </Router> 
                </Provider>  

    )
  }
}
