import React, { Component } from 'react'

export default class Search extends Component {


    constructor(props) {
        super(props);
        this.state = {
            query: '',

        }
        this.searchQuery = this.searchQuery.bind(this);
    }


    searchQuery(e) {
        this.setState({
            query: e.target.value

        })
    }

    render() {
        let findQuery = this.props.data.filter(
            i => {
                return i.description.indexOf(this.state.query) !== -1}
        );




        console.log("The data return @ Search " + JSON.stringify(this.props.data));
        console.log("The found return " + JSON.stringify(findQuery));

        return (
            <div>
                <input
                    onChange={this.searchQuery}
                    type="text"
                    placeholder="Search..."
                />

                <table className="table table-striped"
                    style={{marginTop: 20 }}>
                        <thead>
                            <tr>
                                <th>Description</th>
                                <th>Deadline</th>
                                <th>Assignee</th>
                                <th>Priority</th>
                                <th>Actions</th>
                            </tr>
                        </thead>
                        <tbody>
                            {/* {findQuery.map((i)=> {
                                return <td key={i._id}>{i}</td>

                            })} */}

                        </tbody>


                    </table>    
            </div>
        )
    }
}

