import React, { Component } from 'react'

export default class Search extends Component {


    constructor(props) {
        super(props);
        this.state = {
            query: '',

        }
        this.searchQuery = this.searchQuery.bind(this);
    }


    searchQuery(e) {
        this.setState({
            query: e.target.value

        })
    }

    render() {
        const findQuery = this.props.data.filter(
            i => i.description.includes(this.state.query)
        )

        const returnQuery = findQuery.map(
            i => <table className="table table-striped" style={{marginTop: 20 }}>
                    <thead>
                        <tr key={i._id}>
                            <td>{i.description}</td>
                            <td>{i.deadline}</td>
                            <td>{i.assignee}</td>
                            <td>{i.priority}</td>
                            <td>{i.actions}</td>
                        </tr> 
                    </thead>
                    </table>
        )

        console.log("The data return @ Search " + JSON.stringify(this.props.data));
        console.log("The found return " + JSON.stringify(findQuery));

        return (
            <div>
                <input
                    onChange={this.searchQuery}
                    type="text"
                    placeholder="Search..."
                />

                <table className="table table-striped"
                    style={{marginTop: 20 }}>
                        <thead>
                            <tr>
                                <th>Description</th>
                                <th>Deadline</th>
                                <th>Assignee</th>
                                <th>Priority</th>
                                <th>Actions</th>
                            </tr>
                        </thead>
                        <tbody>
                            {this.returnQuery}

                        </tbody>


                    </table>    
            </div>
        )
    }
}

// get input value in search bar
// add search value into search route
